mapper.select:
SELECT

FROM t_advertisement_info
WHERE
DELETED = 'F'

	AND id = #{id,jdbcType=INTEGER}


	AND ad_code = #{adCode,jdbcType=VARCHAR}


	AND img_url = #{imgUrl,jdbcType=VARCHAR}


	AND ref_url = #{refUrl,jdbcType=VARCHAR}


	AND remarks = #{remarks,jdbcType=VARCHAR}


	AND ad_type = #{adType,jdbcType=VARCHAR}

ORDER BY

	${orderBy} ${sort}


	create_time ${sort}

LIMIT #{offset} , #{pageSize}
