mapper.insert:
INSERT INTO t_coupon_info( id, coupon_code, remarks, create_time, modify_time, deleted, user_code, coupon_type_code, status ) VALUES( #{id,jdbcType=INTEGER}, #{couponCode,jdbcType=VARCHAR}, #{remarks,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{modifyTime,jdbcType=TIMESTAMP}, #{deleted,jdbcType=VARCHAR}, #{userCode,jdbcType=VARCHAR}, #{couponTypeCode,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}
)
select LAST_INSERT_ID()

mapper.select:
SELECT

FROM t_coupon_info
WHERE
DELETED = 'F'

	AND id = #{id,jdbcType=INTEGER}


	AND coupon_code = #{couponCode,jdbcType=VARCHAR}


	AND remarks = #{remarks,jdbcType=VARCHAR}


	AND create_time = #{createTime,jdbcType=TIMESTAMP}


	AND modify_time = #{modifyTime,jdbcType=TIMESTAMP}


	AND user_code = #{userCode,jdbcType=VARCHAR}


	AND coupon_type_code = #{couponTypeCode,jdbcType=VARCHAR}


	AND status = #{status,jdbcType=VARCHAR}

ORDER BY

	${orderBy} ${sort}


	create_time ${sort}

LIMIT #{offset} , #{pageSize}
