DELETED = 'F'

	AND id = #{id,jdbcType=INTEGER}


	AND coupon_type_code = #{couponTypeCode,jdbcType=VARCHAR}


	AND coupon_type_name = #{couponTypeName,jdbcType=VARCHAR}


	AND create_time = #{createTime,jdbcType=TIMESTAMP}


	AND modify_time = #{modifyTime,jdbcType=TIMESTAMP}


	AND num = #{num,jdbcType=INTEGER}


	AND deadline = #{deadline,jdbcType=TIMESTAMP}


	AND scope_type_code = #{scopeTypeCode,jdbcType=VARCHAR}


	AND status = #{status,jdbcType=VARCHAR}


	AND effect_date = #{effectDate,jdbcType=TIMESTAMP}


	AND discount = #{discount,jdbcType=INTEGER}


	AND remind = #{remind,jdbcType=INTEGER}


	AND remarks = #{remarks,jdbcType=VARCHAR}


	AND img_code = #{imgCode,jdbcType=VARCHAR}


	AND conditions = #{conditions,jdbcType=VARCHAR}


	AND consume_amount >= #{consumeAmount,jdbcType=INTEGER}

mapper.select:
SELECT

FROM t_coupon_type
WHERE
DELETED = 'F'

	AND id = #{id,jdbcType=INTEGER}


	AND coupon_type_code = #{couponTypeCode,jdbcType=VARCHAR}


	AND coupon_type_name = #{couponTypeName,jdbcType=VARCHAR}


	AND create_time = #{createTime,jdbcType=TIMESTAMP}


	AND modify_time = #{modifyTime,jdbcType=TIMESTAMP}


	AND num = #{num,jdbcType=INTEGER}


	AND deadline = #{deadline,jdbcType=TIMESTAMP}


	AND scope_type_code = #{scopeTypeCode,jdbcType=VARCHAR}


	AND status = #{status,jdbcType=VARCHAR}


	AND effect_date = #{effectDate,jdbcType=TIMESTAMP}


	AND discount = #{discount,jdbcType=INTEGER}


	AND remind = #{remind,jdbcType=INTEGER}


	AND remarks = #{remarks,jdbcType=VARCHAR}


	AND img_code = #{imgCode,jdbcType=VARCHAR}


	AND conditions = #{conditions,jdbcType=VARCHAR}


	AND consume_amount >= #{consumeAmount,jdbcType=INTEGER}

ORDER BY

	${orderBy} ${sort}


	create_time ${sort}

LIMIT #{offset} , #{pageSize}
