mapper.insert:
INSERT INTO t_manager_info( id, manager_code, manager_name, remarks, deleted, create_time, modify_time, password ) VALUES( #{id,jdbcType=INTEGER}, #{managerCode,jdbcType=VARCHAR}, #{managerName,jdbcType=VARCHAR}, #{remarks,jdbcType=VARCHAR}, #{deleted,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{modifyTime,jdbcType=TIMESTAMP}, #{password,jdbcType=VARCHAR}
)
