mapper.insert:
INSERT INTO t_student_info( id, student_code, student_name, age, sex, user_code, create_time, modify_time, deleted, school, height, weight ) VALUES( #{id,jdbcType=INTEGER}, #{studentCode,jdbcType=VARCHAR}, #{studentName,jdbcType=VARCHAR}, #{age,jdbcType=INTEGER}, #{sex,jdbcType=VARCHAR}, #{userCode,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{modifyTime,jdbcType=TIMESTAMP}, #{deleted,jdbcType=VARCHAR}, #{school,jdbcType=VARCHAR}, #{height,jdbcType=INTEGER}, #{weight,jdbcType=INTEGER}
)
select LAST_INSERT_ID()

mapper.select:
SELECT

FROM t_student_info
WHERE
DELETED = 'F'

	AND user_code = #{userCode,jdbcType=VARCHAR}

ORDER BY

	${orderBy} ${sort}


	create_time ${sort}

LIMIT #{offset} , #{pageSize}
