mapper.select:
SELECT

FROM t_trainer_info
WHERE
DELETED = 'F'

	AND id = #{id,jdbcType=INTEGER}


	AND school_name = #{schoolName,jdbcType=VARCHAR}


	AND experience = #{experience,jdbcType=INTEGER}


	AND honors = #{honors,jdbcType=VARCHAR}


	AND trainer_code = #{trainerCode,jdbcType=VARCHAR}


	AND create_time = #{createTime,jdbcType=TIMESTAMP}


	AND modify_time = #{modifyTime,jdbcType=TIMESTAMP}


	AND persion_sign = #{persionSign,jdbcType=VARCHAR}


	AND trainer_name = #{trainerName,jdbcType=VARCHAR}


	AND img_url = #{imgUrl,jdbcType=VARCHAR}


	AND sex = #{sex,jdbcType=VARCHAR}


	AND teachExps = #{teachExps,jdbcType=VARCHAR}

ORDER BY

	${orderBy} ${sort}


	create_time ${sort}

LIMIT #{offset} , #{pageSize}
