	AND user_code = #{userCode,jdbcType=VARCHAR}


	AND create_time = #{createTime,jdbcType=TIMESTAMP}


	AND modify_time = #{modifyTime,jdbcType=TIMESTAMP}


	AND telephone = #{telephone,jdbcType=VARCHAR}


	AND age = #{age,jdbcType=INTEGER}


