sql.where:
DELETED = 'F'

	AND activity_code = #{activityCode,jdbcType=VARCHAR}


	AND activity_name = #{activityName,jdbcType=VARCHAR}


	AND activity_type_name =
	#{activityTypeName,jdbcType=VARCHAR}



	AND status = #{status,jdbcType=VARCHAR}


	AND activity_rank = #{activityRank,jdbcType=INTEGER}

mapper.update:
UPDATE t_activity_info set
status = #{status,jdbcType=VARCHAR} ,
activity_code = #{activityCode,jdbcType=VARCHAR} WHERE activity_code = #{activityCode,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM t_activity_info
WHERE
DELETED = 'F'

ORDER BY

	${orderBy} ${sort}


	create_time ${sort}

LIMIT #{offset} , #{pageSize}
