mapper.select:
SELECT

FROM t_course_type
WHERE
DELETED = 'F'

	AND course_type_code = #{courseTypeCode,jdbcType=VARCHAR}


	AND course_type_name = #{courseTypeName,jdbcType=VARCHAR}

ORDER BY

	${orderBy} ${sort}


	create_time ${sort}

LIMIT #{offset} , #{pageSize}
