mapper.insert:
INSERT INTO t_order_info( id, user_code, goods_code, num, price, amount, status, order_no, create_time, modify_time, deleted, remarks, charge_id, order_type, student_code, coupon_code, is_free, is_online, book_time ) VALUES( #{id,jdbcType=INTEGER}, #{userCode,jdbcType=VARCHAR}, #{goodsCode,jdbcType=VARCHAR}, #{num,jdbcType=INTEGER}, #{price,jdbcType=INTEGER}, #{amount,jdbcType=INTEGER}, #{status,jdbcType=VARCHAR}, #{orderNo,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{modifyTime,jdbcType=TIMESTAMP}, #{deleted,jdbcType=VARCHAR}, #{remarks,jdbcType=VARCHAR}, #{chargeId,jdbcType=VARCHAR}, #{orderType,jdbcType=VARCHAR}, #{studentCode,jdbcType=VARCHAR}, #{couponCode,jdbcType=VARCHAR}, #{isFree,jdbcType=VARCHAR}, #{isOnline,jdbcType=VARCHAR}, #{bookTime,jdbcType=VARCHAR}
)
select LAST_INSERT_ID()

mapper.select:
SELECT

FROM t_order_info
WHERE
DELETED = 'F'


	AND user_code = #{userCode,jdbcType=VARCHAR}


	AND goods_code = #{goodsCode,jdbcType=VARCHAR}



	AND status = #{status,jdbcType=VARCHAR}


	AND order_no = #{orderNo,jdbcType=VARCHAR}



	AND order_type like #{orderType,jdbcType=VARCHAR}


	AND student_code = #{studentCode,jdbcType=VARCHAR}


	AND coupon_code = #{couponCode,jdbcType=VARCHAR}


	AND is_free = #{isFree,jdbcType=VARCHAR}


	AND is_online = #{isOnline,jdbcType=VARCHAR}


	AND book_time = #{bookTime,jdbcType=VARCHAR}

ORDER BY

	${orderBy} ${sort}


	create_time ${sort}

LIMIT #{offset} , #{pageSize}
